-- Create database and user ID
CREATE DATABASE IF NOT EXISTS bookbase_db;
DROP USER IF EXISTS 'bookbase_user'@'localhost';

CREATE USER 'bookbase_user'@'localhost' IDENTIFIED WITH mysql_native_password BY 'password123';

GRANT ALL PRIVILEGES ON bookbase_db.* TO 'bookbase_user'@'localhost';

FLUSH PRIVILEGES;

USE bookbase_db;

-- Create books table
CREATE TABLE IF NOT EXISTS books (
    id INT AUTO_INCREMENT PRIMARY KEY,
    title VARCHAR(255) NOT NULL,
    author VARCHAR(255) NOT NULL,
    isbn VARCHAR(20) UNIQUE,
    publisher VARCHAR(255),
    year INT,
    genre VARCHAR(100),
    rating INT CHECK (rating >= 1 AND rating <= 5),
    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP
);

-- Create index values for better performance
CREATE INDEX idx_title ON books(title);
CREATE INDEX idx_author ON books(author);
CREATE INDEX idx_genre ON books(genre);
CREATE INDEX idx_year ON books(year);
CREATE INDEX idx_rating ON books(rating);


